import React, { useState } from "react";



function App() {
  const [count,setCount] = useState(0)


function increasceCount(){
  setCount(count +1)

}

function decreasceCount() {
  setCount(count-1)
}
  return (
  <div className="container">
  <h1>{count}</h1>
  <button onClick = {increasceCount}>+</button>
  <button onClick = {decreasceCount}>-</button>
</div>)
}

export default App;
