import React, { useState } from "react";



const App = () => {
  // Utilizing React hooks for count variable in order to render automatically
  const [count,setCount] = useState(0)

  // funtion to incresace count
   const increasceCount = () => setCount(count +1)


  // function to decreasce count
  const decreasceCount = () => setCount(count -1)


  return (
  <div className="container">
    <h1>{count}</h1>
    <button onClick = {increasceCount}>+</button>
    <button onClick = {decreasceCount}>-</button>
  </div>)
}

export default App;
