import React, { useState } from "react";



const App = () => {

  const now = new Date().toLocaleTimeString("en-uis")
  console.log(now)


  const [timeNow, getCurrentTime] = useState(now)

  function getTime(){ 

    getCurrentTime(timeNow = now )


  }

  // setInterval(getTime,1000)
  return (
      <div className="container">
        <h1>{timeNow}</h1>
        <button onClick = {getTime} >Get Time</button>
      </div>
     );

     }

export default App;
