import React, { useState } from "react";



const App = () => {



  const [timeNow, getCurrentTime] = useState(0)

  function getTime(){ 

    getCurrentTime(timeNow = new Date().getHours() + ":" + new Date().getMinutes() + ":" + new Date().getSeconds())


  }

  setInterval(getTime,1000)
  return (
      <div className="container">
        <h1>{timeNow}</h1>
        <button onClick = {getTime} >Get Time</button>
      </div>
     );

     }

export default App;
